import React, { useState, useEffect } from 'react';
import { getDatabase, ref, get } from 'firebase/database';

const Converse = () => {
  const [converses, setConverses] = useState([]);

  const fetchConverseData = async () => {
    const db = getDatabase();
    const converseRef = ref(db, 'converses/');
    try {
      const snapshot = await get(converseRef);
      if (snapshot.exists()) {
        setConverses(Object.values(snapshot.val()));
      } else {
        console.log('No data available');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchConverseData();
  }, []);

  return (
    <div>
      <h1>Converse Events</h1>
      {converses.length > 0 ? (
        <ul>
          {converses.map((converse, index) => (
            <li key={index}>
              <h2>{converse.year}</h2>
              <p>Event Date: {converse.eventDate}</p>
              <p>Faculty Coordinator: {converse.facultyCo}</p>
              <p>Student Coordinator: {converse.studentCo}</p>
              {converse.reportUrl && (
                <p>
                  <a href={converse.reportUrl} target="_blank" rel="noopener noreferrer">
                    View Report
                  </a>
                </p>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No converse events available.</p>
      )}
    </div>
  );
};

export default Converse;
