import React, { useEffect, useState } from 'react';
import { getDatabase, ref, onValue } from 'firebase/database';

function ShowAnalysis() {
  const [data, setData] = useState([]);
  const [semesters, setSemesters] = useState([]);
  const [years, setYears] = useState([]);

  useEffect(() => {
    const db = getDatabase();
    const resultsRef = ref(db, 'analysis');

    onValue(resultsRef, (snapshot) => {
      const results = snapshot.val();
      const resultsArray = results ? Object.keys(results).map(key => ({ id: key, ...results[key] })) : [];
      setData(resultsArray);

      // Extract unique semesters and years for headers
      const uniqueSemesters = Array.from(new Set(resultsArray.flatMap(item => Object.keys(item.semesters))));
      const uniqueYears = Array.from(new Set(resultsArray.map(item => item.year)));
      
      setSemesters(uniqueSemesters);
      setYears(uniqueYears);
    });
  }, []);

  return (
    <div className="analysis-container">
      <h1>Result Analysis</h1>
      <table className="result-table">
        <thead>
          <tr>
            <th>Admission Year</th>
            {semesters.map(semester => (
              <th key={semester}>{semester}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {years.map(year => (
            <tr key={year}>
              <td>{year}</td>
              {semesters.map(semester => (
                <td key={`${year}-${semester}`}>
                  {data.find(item => item.year === year && item.semesters[semester]) ? (
                    <a 
                      href={data.find(item => item.year === year && item.semesters[semester]).semesters[semester]} 
                      target="_blank" 
                      rel="noopener noreferrer">
                      <img src="/path-to-your-icon/pdf-icon.png" alt="PDF Icon" />
                    </a>
                  ) : (
                    <span> - </span>
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ShowAnalysis;
