import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import { collection, getDocs } from 'firebase/firestore';
import './AddResult.css';

const ShowCalendar = () => {
  const [calendars, setCalendars] = useState([]);

  useEffect(() => {
    const fetchCalendars = async () => {
      const querySnapshot = await getDocs(collection(db, 'calendars'));
      const calendarList = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCalendars(calendarList);
    };

    fetchCalendars();
  }, []);

  return (
    <div className="add-result-container">
      <h2 className="add-result-header">Academic Calendar Details</h2>
      {calendars.map(calendar => (
        <div key={calendar.id} className="add-result-form">
          <p><strong>Calendar Type:</strong> {calendar.calendarType}</p>
          <p><strong>Academic Year:</strong> {calendar.academicYear}</p>
          <p><strong>Semester:</strong> {calendar.semester}</p>
          {calendar.pdf && (
            <div>
              <strong>View (PDF):</strong>
              <a href={calendar.pdf} target="_blank" rel="noopener noreferrer">
                View PDF
              </a>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default ShowCalendar;
