import React, { useState, useEffect } from 'react';
import { getDatabase, ref, get } from 'firebase/database';
import './showind.css';

const ShowInd = () => {
  const [visits, setVisits] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    const db = getDatabase();
    const indRef = ref(db, 'industrialVisits');

    get(indRef)
      .then((snapshot) => {
        setLoading(false);
        if (snapshot.exists()) {
          const data = snapshot.val();
          const formattedData = Object.keys(data).map((key) => data[key]);
          setVisits(formattedData);
        } else {
          setVisits([]);
        }
      })
      .catch((error) => {
        setLoading(false);
        setError('Error fetching visits: ' + error.message);
      });
  }, []);

  return (
    <div className="show-industrial-visits">
      <h2>Industrial Visits</h2>
      {loading && <p>Loading visits...</p>}
      {error && <p className="error-message">{error}</p>}
      {visits.length > 0 ? (
        <ul>
          {visits.map((visit, index) => (
            <li key={index}>
              <p>Visit Date: {visit.visitDate}</p>
              <p>Place of Visit: {visit.placeOfVisit}</p>
              <p>Faculty: {visit.faculty}</p>
              {visit.reportUrl && (
                <a href={visit.reportUrl} target="_blank" rel="noopener noreferrer">
                  View Report
                </a>
              )}
            </li>
          ))}
        </ul>
      ) : (
        !loading && <p>No visits found.</p>
      )}
    </div>
  );
};

export default ShowInd;
