import React, { useState, useEffect } from 'react';
import { getDatabase, ref, query, orderByChild, equalTo, get } from 'firebase/database';
import './Result.css';

function Result() {
  const [type, setType] = useState('');
  const [resultData, setResultData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleTypeChange = (selectedType) => {
    setType(selectedType);
  };

  useEffect(() => {
    if (type) {
      setLoading(true);
      setError(null);

      const db = getDatabase();
      const resultsRef = ref(db, 'results');
      const typeQuery = query(resultsRef, orderByChild('type'), equalTo(type));

      get(typeQuery)
        .then((snapshot) => {
          setLoading(false);
          if (snapshot.exists()) {
            const data = snapshot.val();
            const formattedData = Object.keys(data).map((key) => data[key]);
            setResultData(formattedData);
          } else {
            setResultData([]);
          }
        })
        .catch((error) => {
          setLoading(false);
          setError('Error fetching result data: ' + error.message);
        });
    }
  }, [type]);

  return (
    <div className="result-container">
      <h2 className="result-header">Results</h2>

      <div className="type-buttons">
        <div
          className={`type-button ${type === 'Internal' ? 'selected' : ''}`}
          onClick={() => handleTypeChange('Internal')}
        >
          <span>INTERNAL</span>
        </div>
        <div
          className={`type-button ${type === 'External' ? 'selected' : ''}`}
          onClick={() => handleTypeChange('External')}
        >
          <span>EXTERNAL</span>
        </div>
        <div
          className={`type-button ${type === 'Midterm' ? 'selected' : ''}`}
          onClick={() => handleTypeChange('Midterm')}
        >
          <span>MID TERM</span>
        </div>
      </div>

      {loading && <p className="loading-message">Loading results...</p>}
      {error && <p className="error-message">{error}</p>}

      {resultData.length > 0 ? (
        <table className="result-table">
          <thead>
            <tr>
              <th>Type</th>
              <th>Year</th>
              <th>Classname</th>
              <th>Schedule PDF</th>
              <th>Seating PDF</th>
              <th>Result PDF</th>
            </tr>
          </thead>
          <tbody>
            {resultData.map((item, index) => (
              <tr key={index}>
                <td>{item.type}</td>
                <td>{item.year}</td>
                <td>{item.classname}</td>
                <td>
                  <a href={item.schedulePdfUrl} target="_blank" rel="noopener noreferrer">
                    View Schedule
                  </a>
                </td>
                <td>
                  <a href={item.seatingPdfUrl} target="_blank" rel="noopener noreferrer">
                    View Seating
                  </a>
                </td>
                <td>
                  <a href={item.resultPdfUrl} target="_blank" rel="noopener noreferrer">
                    View Result
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : !loading && !error && (
        <p className="no-data-message">No results found for the selected type.</p>
      )}
    </div>
  );
}

export default Result;
