import React, { useState } from 'react';
import { getDatabase, ref, push } from 'firebase/database';
import './AddClassTimetable.css'; // Import the CSS file

const AddClassTimetable = () => {
  const [no, setNo] = useState('');
  const [semester, setSemester] = useState('');
  const [pdfUrl, setPdfUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const db = getDatabase();
    const timetableRef = ref(db, 'classTimetables');

    const newTimetable = {
      no,
      semester,
      pdfUrl,
    };

    push(timetableRef, newTimetable)
      .then(() => {
        setLoading(false);
        setNo('');
        setSemester('');
        setPdfUrl('');
      })
      .catch((error) => {
        setLoading(false);
        setError('Error adding timetable: ' + error.message);
      });
  };

  return (
    <div className="add-class-timetable">
      <h2>Add Class Timetable</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>No:</label>
          <input
            type="text"
            value={no}
            onChange={(e) => setNo(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Semester:</label>
          <input
            type="text"
            value={semester}
            onChange={(e) => setSemester(e.target.value)}
            required
          />
        </div>
        <div>
          <label>PDF URL:</label>
          <input
            type="url"
            value={pdfUrl}
            onChange={(e) => setPdfUrl(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Timetable'}
        </button>
      </form>
    </div>
  );
};

export default AddClassTimetable;
