import React, { useState, useEffect } from 'react';
import { getDatabase, ref, query, orderByChild, equalTo, get } from 'firebase/database';
import './Result.css';

function Result() {
  const [type, setType] = useState('');
  const [season, setSeason] = useState('');
  const [admissionYear, setAdmissionYear] = useState('');
  const [resultData, setResultData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleTypeChange = (selectedType) => {
    setType(selectedType);
  };

  const handleSeasonChange = (e) => {
    setSeason(e.target.value);
  };

  const handleAdmissionYearChange = (e) => {
    setAdmissionYear(e.target.value);
  };

  useEffect(() => {
    if (type || season || admissionYear) {
      setLoading(true);
      setError(null);

      const db = getDatabase();
      const resultsRef = ref(db, 'results');
      let typeQuery = query(resultsRef);

      if (type) {
        typeQuery = query(typeQuery, orderByChild('type'), equalTo(type));
      }
      if (season) {
        typeQuery = query(typeQuery, orderByChild('season'), equalTo(season));
      }
      if (admissionYear) {
        typeQuery = query(typeQuery, orderByChild('admissionYear'), equalTo(admissionYear));
      }

      get(typeQuery)
        .then((snapshot) => {
          setLoading(false);
          if (snapshot.exists()) {
            const data = snapshot.val();
            const formattedData = Object.keys(data).map((key) => data[key]);
            setResultData(formattedData);
          } else {
            setResultData([]);
          }
        })
        .catch((error) => {
          setLoading(false);
          setError('Error fetching result data: ' + error.message);
        });
    }
  }, [type, season, admissionYear]);

  return (
    <div className="result-container">
      <h2 className="result-header">Results</h2>

      <div className="type-buttons">
        <div
          className={`type-button ${type === 'Internal' ? 'selected' : ''}`}
          onClick={() => handleTypeChange('Internal')}
        >
          <span>INTERNAL</span>
        </div>
        <div
          className={`type-button ${type === 'External' ? 'selected' : ''}`}
          onClick={() => handleTypeChange('External')}
        >
          <span>EXTERNAL</span>
        </div>
        <div
          className={`type-button ${type === 'Midterm' ? 'selected' : ''}`}
          onClick={() => handleTypeChange('Midterm')}
        >
          <span>MID TERM</span>
        </div>
      </div>

      <div className="filter-select">
        <label>Select Season:</label>
        <select value={season} onChange={handleSeasonChange}>
          <option value="">Select</option>
          <option value="Summer">Summer</option>
          <option value="Winter">Winter</option>
        </select>
      </div>

      <div className="filter-select">
        <label>Select Type:</label>
        <select value={type} onChange={(e) => handleTypeChange(e.target.value)}>
          <option value="">Select</option>
          <option value="Regular">Regular</option>
          <option value="Backlog">Backlog</option>
        </select>
      </div>

      <div className="filter-select">
        <label>Select Admission Year:</label>
        <input
          type="number"
          value={admissionYear}
          onChange={handleAdmissionYearChange}
          placeholder="Enter year"
        />
      </div>

      {loading && <p className="loading-message">Loading results...</p>}
      {error && <p className="error-message">{error}</p>}

      {resultData.length > 0 ? (
        <table className="result-table">
          <thead>
            <tr>
              <th>Type</th>
              <th>Year</th>
              <th>Classname</th>
              <th>Schedule PDF</th>
              <th>Seating PDF</th>
              <th>Result PDF</th>
            </tr>
          </thead>
          <tbody>
            {resultData.map((item, index) => (
              <tr key={index}>
                <td>{item.type}</td>
                <td>{item.year}</td>
                <td>{item.classname}</td>
                <td>
                  <a href={item.schedulePdfUrl} target="_blank" rel="noopener noreferrer">
                    View Schedule
                  </a>
                </td>
                <td>
                  <a href={item.seatingPdfUrl} target="_blank" rel="noopener noreferrer">
                    View Seating
                  </a>
                </td>
                <td>
                  <a href={item.resultPdfUrl} target="_blank" rel="noopener noreferrer">
                    View Result
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : !loading && !error && (
        <p className="no-data-message">No results found for the selected filters.</p>
      )}
    </div>
  );
}

export default Result;
