import React, { useState } from 'react';
import { db } from './firebase';
import { collection, addDoc } from 'firebase/firestore';
import './AddResult.css';

const AddCalendar = () => {
  const [calendarType, setCalendarType] = useState('');
  const [academicYear, setAcademicYear] = useState('');
  const [semester, setSemester] = useState('');
  const [pdf, setPdf] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const docRef = await addDoc(collection(db, 'calendars'), {
        calendarType,
        academicYear,
        semester,
        pdf: pdf ? pdf.name : null,
      });
      console.log('Document written with ID: ', docRef.id);
    } catch (e) {
      console.error('Error adding document: ', e);
    }
  };

  return (
    <div className="add-result-container">
      <h2 className="add-result-header">Add Academic Calendar</h2>
      <form className="add-result-form" onSubmit={handleSubmit}>
        <label>
          Calendar Type:
          <select value={calendarType} onChange={(e) => setCalendarType(e.target.value)}>
            <option value="">Select Type</option>
            <option value="University Academic Calendar">University Academic Calendar</option>
            <option value="College Academic Calendar">College Academic Calendar</option>
            <option value="Department Academic Calendar">Department Academic Calendar</option>
          </select>
        </label>
        <label>
          Academic Year:
          <input
            type="text"
            value={academicYear}
            onChange={(e) => setAcademicYear(e.target.value)}
            placeholder="e.g., 2023-2024"
          />
        </label>
        <label>
          Semester:
          <input
            type="text"
            value={semester}
            onChange={(e) => setSemester(e.target.value)}
            placeholder="e.g., Fall"
          />
        </label>
        <label>
          View (PDF):
          <input
            type="file"
            accept="application/pdf"
            onChange={(e) => setPdf(e.target.files[0])}
          />
        </label>
        <button type="submit">Add Calendar</button>
      </form>
    </div>
  );
};

export default AddCalendar;
