import React, { useState, useEffect } from 'react';
import { getDatabase, ref, get } from 'firebase/database';
import './Calendar.css';

const ShowCalendar = () => {
  const [calendarData, setCalendarData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchCalendarData = async () => {
    setLoading(true);
    setError(null);
    const db = getDatabase();
    const calendarRef = ref(db, 'calendars/');
    try {
      const snapshot = await get(calendarRef);
      setLoading(false);
      if (snapshot.exists()) {
        const data = snapshot.val();
        const formattedData = Object.keys(data).map(key => ({
          id: key,
          ...data[key]
        }));
        setCalendarData(formattedData);
      } else {
        setCalendarData([]);
        console.log('No data available');
      }
    } catch (error) {
      setLoading(false);
      console.error('Error fetching data:', error);
      setError('Error fetching data: ' + error.message);
    }
  };

  useEffect(() => {
    fetchCalendarData();
  }, []);

  return (
    <div className="show-calendar-container">
      <h1>Academic Calendars</h1>
      {loading && <p className="loading-message">Loading calendar data...</p>}
      {error && <p className="error-message">{error}</p>}
      {calendarData.length > 0 ? (
        <ul>
          {calendarData.map((calendar) => (
            <li key={calendar.id}>
              <h2>{calendar.calendarType}</h2>
              <p>Academic Year: {calendar.academicYear}</p>
              <p>Semester: {calendar.semester}</p>
              {calendar.pdfUrl && (
                <a href={calendar.pdfUrl} target="_blank" rel="noopener noreferrer">
                  View PDF
                </a>
              )}
            </li>
          ))}
        </ul>
      ) : !loading && !error && (
        <p>No calendars available</p>
      )}
    </div>
  );
};

export default ShowCalendar;
