import React, { useState, useEffect } from 'react';
import { getDatabase, ref, query, orderByChild, equalTo, get } from 'firebase/database';

function Result() {
  const [type, setType] = useState('');
  const [resultData, setResultData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleTypeChange = (e) => {
    setType(e.target.value);
  };

  useEffect(() => {
    if (type) {
      setLoading(true);
      setError(null);
      
      const db = getDatabase();
      const resultsRef = ref(db, 'results');
      const typeQuery = query(resultsRef, orderByChild('type'), equalTo(type));

      get(typeQuery)
        .then((snapshot) => {
          setLoading(false);
          if (snapshot.exists()) {
            const data = snapshot.val();
            const formattedData = Object.keys(data).map((key) => data[key]);
            setResultData(formattedData);
          } else {
            setResultData([]);
          }
        })
        .catch((error) => {
          setLoading(false);
          setError('Error fetching result data: ' + error.message);
        });
    }
  }, [type]);

  return (
    <div>
      <h2>Results</h2>
      <div>
        <label>Select Type:</label>
        <select value={type} onChange={handleTypeChange}>
          <option value="">Select</option>
          <option value="internal">Internal</option>
          <option value="external">External</option>
          <option value="midterm">Midterm</option>
        </select>
      </div>
      
      {loading && <p>Loading results...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      {resultData.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Type</th>
              <th>Year</th>
              <th>Classname</th>
              <th>Schedule PDF</th>
              <th>Seating PDF</th>
              <th>Result PDF</th>
            </tr>
          </thead>
          <tbody>
            {resultData.map((item, index) => (
              <tr key={index}>
                <td>{item.type}</td>
                <td>{item.year}</td>
                <td>{item.classname}</td>
                <td>
                  <a href={item.schedulePdfUrl} target="_blank" rel="noopener noreferrer">
                    View Schedule
                  </a>
                </td>
                <td>
                  <a href={item.seatingPdfUrl} target="_blank" rel="noopener noreferrer">
                    View Seating
                  </a>
                </td>
                <td>
                  <a href={item.resultPdfUrl} target="_blank" rel="noopener noreferrer">
                    View Result
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      
      {resultData.length === 0 && !loading && !error && <p>No results found for the selected type.</p>}
    </div>
  );
}

export default Result;
