import React, { useState } from 'react';
import { getFirestore, collection, addDoc } from 'firebase/firestore';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { useFirebaseApp } from 'reactfire';
// import './AddConverse.css';

const AddConverse = () => {
  const [year, setYear] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [facultyCo, setFacultyCo] = useState('');
  const [studentCo, setStudentCo] = useState('');
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const firebaseApp = useFirebaseApp();
  const firestore = getFirestore(firebaseApp);
  const storage = getStorage(firebaseApp);

  const handleFileChange = (e) => {
    setReport(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      let reportUrl = '';

      if (report) {
        const reportRef = ref(storage, `reports/${report.name}`);
        await uploadBytes(reportRef, report);
        reportUrl = await getDownloadURL(reportRef);
      }

      await addDoc(collection(firestore, 'converses'), {
        year,
        eventDate,
        facultyCo,
        studentCo,
        reportUrl,
      });

      alert('Converse added successfully!');
      setYear('');
      setEventDate('');
      setFacultyCo('');
      setStudentCo('');
      setReport(null);
    } catch (err) {
      setError('Failed to add converse.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-converse">
      <h2>Add Converse</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="year">Year</label>
          <input
            type="text"
            id="year"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="eventDate">Event Date</label>
          <input
            type="text"
            id="eventDate"
            value={eventDate}
            onChange={(e) => setEventDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="facultyCo">Faculty Coordinator</label>
          <input
            type="text"
            id="facultyCo"
            value={facultyCo}
            onChange={(e) => setFacultyCo(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="studentCo">Student Coordinator</label>
          <input
            type="text"
            id="studentCo"
            value={studentCo}
            onChange={(e) => setStudentCo(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="report">Report (PDF)</label>
          <input
            type="file"
            id="report"
            accept=".pdf"
            onChange={handleFileChange}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Submitting...' : 'Submit'}
        </button>
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
};

export default AddConverse;
