import React, { useState, useEffect } from "react";
import { Route, Routes } from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Navbar from "./components/Navbar";
import AboutUs from "./components/AboutUs";
import Message from "./components/Message";
import Vision from "./components/Vision";
import Mission from "./components/Mission";
import Po from "./components/Po";
import Pso from "./components/Pso";
import Peo from "./components/Peo";
import Login from './auth/Login';
import Register from "./components/Register";
import Manage from "./components/Manage";
import UsersPage from "./components/UsersPage";
import "./App.css";
import axios from 'axios';

const images = [
  "https://itai.escet.online/images/slidernew/07.jpg",
  "https://itai.escet.online/images/slidernew/08.jpg",
  "https://itai.escet.online/images/slidernew/07.jpg",
];

function Slider() {
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Change slide every 3 seconds
    return () => clearInterval(interval);
  }, []);
  return (
    <div className="slider">
      {images.map((image, index) => (
        <div
          key={index}
          className={`slide ${index === currentIndex ? "active" : ""}`}
        >
          <img src={image} className="home-img" alt={`Slide ${index + 1}`} />
        </div>
      ))}
    </div>
  );
}

function UpdateUser({ userId }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);

  const handleUpdate = async () => {
    try {
      const response = await axios.put(`/api/users/${userId}`, { name, email });
      console.log('User updated:', response.data);
      setError(null); // Clear any previous errors
    } catch (error) {
      if (error.response) {
        // Server responded with a status other than 200 range
        setError(error.response.data.error);
      } else if (error.request) {
        // Request was made but no response received
        setError('No response from server');
      } else {
        // Something else happened
        setError('Error updating user');
      }
    }
  };

  return (
    <div>
      <h2>Update User</h2>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Name"
      />
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
      />
      <button onClick={handleUpdate}>Update</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <Header />
      <Navbar />
      <div className="content">
        <Routes>
          <Route path="/" element={<Slider />} />
          <Route path="/AboutUs" element={<AboutUs />} />
          <Route path="/Message" element={<Message />} />
          <Route path="/Vision" element={<Vision />} />
          <Route path="/Mission" element={<Mission />} />
          <Route path="/Peo" element={<Peo />} />
          <Route path="/Po" element={<Po />} />
          <Route path="/Pso" element={<Pso />} />
          <Route path="/login" element={<Login />} />
          <Route path="/Register" element={<Register />} />
          <Route path="/Manage" element={<Manage />} />
          <Route path="/userspage" element={<UsersPage />} />
          {/* <Route path="/update-user/:id" element={<UpdateUser userId={1} />} /> Example userId */}
        </Routes>
      </div>
      <Footer />
    </div>
  );
}

export default App;
