import React, { useEffect, useState } from 'react';
import { getDatabase, ref, onValue } from 'firebase/database';

function Analysis() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [selectedSemester, setSelectedSemester] = useState('');

  useEffect(() => {
    const db = getDatabase();
    const resultsRef = ref(db, 'results');

    onValue(resultsRef, (snapshot) => {
      const results = snapshot.val();
      const resultsArray = results ? Object.keys(results).map(key => ({ id: key, ...results[key] })) : [];
      setData(resultsArray);
    });
  }, []);

  useEffect(() => {
    if (selectedSemester) {
      setFilteredData(data.filter(item => item.semester === selectedSemester));
    } else {
      setFilteredData(data);
    }
  }, [selectedSemester, data]);

  return (
    <div>
      <h1>Analysis</h1>
      <div>
        <label>Filter by Semester:</label>
        <select value={selectedSemester} onChange={(e) => setSelectedSemester(e.target.value)}>
          <option value="">All</option>
          {Array.from(new Set(data.map(item => item.semester))).map(semester => (
            <option key={semester} value={semester}>{semester}</option>
          ))}
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Year</th>
            <th>Semester</th>
            <th>Result PDF</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map(item => (
            <tr key={item.id}>
              <td>{item.year}</td>
              <td>{item.semester}</td>
              <td><a href={item.resultPdfUrl} target="_blank" rel="noopener noreferrer">View PDF</a></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Analysis;
