import React, { useState, useEffect } from 'react';
import { getDatabase, ref, query, orderByChild, equalTo, get } from 'firebase/database';

function Syllabus() {
  const [semester, setSemester] = useState('');
  const [syllabusData, setSyllabusData] = useState([]);

  const handleSemesterChange = (e) => {
    setSemester(e.target.value);
  };

  useEffect(() => {
    if (semester) {
      const db = getDatabase();
      const syllabusRef = ref(db, 'syllabus');
      const semesterQuery = query(syllabusRef, orderByChild('semester'), equalTo(semester));

      get(semesterQuery)
        .then((snapshot) => {
          if (snapshot.exists()) {
            const data = snapshot.val();
            const formattedData = Object.keys(data).map((key) => data[key]);
            setSyllabusData(formattedData);
          } else {
            setSyllabusData([]);
          }
        })
        .catch((error) => console.error('Error fetching syllabus data:', error));
    }
  }, [semester]);

  return (
    <div>
      <h2>Syllabus</h2>
      <div>
        <label>Select Semester:</label>
        <select value={semester} onChange={handleSemesterChange}>
          <option value="">Select</option>
          {[...Array(8).keys()].map((i) => (
            <option key={i + 1} value={i + 1}>
              Semester {i + 1}
            </option>
          ))}
        </select>
      </div>
      {syllabusData.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Subcode</th>
              <th>Name</th>
              <th>Category</th>
              <th>Effective</th>
              <th>L</th>
              <th>T</th>
              <th>P</th>
              <th>Credit</th>
              <th>PDF</th>
            </tr>
          </thead>
          <tbody>
            {syllabusData.map((item) => (
              <tr key={item.subcode}>
                <td>{item.subcode}</td>
                <td>{item.name}</td>
                <td>{item.category}</td>
                <td>{item.effective}</td>
                <td>{item.l}</td>
                <td>{item.t}</td>
                <td>{item.p}</td>
                <td>{item.credit}</td>
                <td>
                  <a href={item.pdf} target="_blank" rel="noopener noreferrer">
                    View PDF
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Syllabus;
