  const [data, setData] = useState('');
  const [file, setFile] = useState(null);

  const handleOperationChange = (e) => {
    setOperation(e.target.value);
  };

  const handleDataChange = (e) => {
    setData(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('operation', operation);
    formData.append('data', data);
    if (file) {
      formData.append('file', file);
    }

    try {
      const response = await fetch('/manage', {
        method: 'POST',
        body: formData,
      });
      if (response.ok) {
        alert('Operation successful');
      } else {
        alert('Operation failed');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Operation failed');
    }
  };

  return (
    <div>
      <h2>Manage</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Operation:</label>
          <select value={operation} onChange={handleOperationChange}>
            <option value="">Select Operation</option>
            <option value="uploadImage">Upload Image</option>
            <option value="uploadSyllabus">Upload Syllabus</option>
            <option value="uploadResult">Upload Result</option>
            <option value="uploadExam">Upload Exam</option>
          </select>
        </div>
        <div>
          <label>Data:</label>
          <input type="text" value={data} onChange={handleDataChange} required />
        </div>
        {operation === 'uploadImage' || operation === 'uploadSyllabus' ? (
          <div>
            <label>File:</label>
            <input type="file" onChange={handleFileChange} />
          </div>
        ) : null}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Manage;
