import React from "react";
import { useForm } from "react-hook-form";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import "./Login.css";
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    try {
      const response = await axios.post("http://localhost:5173/login", data);
      if (response.data.success) {
        // Handle successful login
        sessionStorage.setItem('user', JSON.stringify(response.data.user));
        navigate('/manage');
      } else {
        // Redirect to register page if user does not exist
        navigate('/register');
      }
    } catch (error) {
      console.error("Error logging in", error);
    }
  };

  const handleGoogleSuccess = async (response) => {
    try {
      const googleData = { token: response.credential };
      const result = await axios.post("http://localhost:5173/google-login", googleData);
      if (result.data.success) {
        // Handle successful login
        sessionStorage.setItem('user', JSON.stringify(result.data.user));
        navigate('/manage');
      } else {
        // Redirect to register page if user does not exist
        navigate('/register');
      }
    } catch (error) {
      console.error("Google login error", error);
    }
  };

  const handleGoogleFailure = (error) => {
    console.error("Google login failed", error);
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit(onSubmit)} className="login-form">
        <h2>Login</h2>
        <div>
          <label>Email</label>
          <input
            type="email"
            {...register("email", {
              required: "Email is required",
              pattern: {
                value: /^[a-zA-Z0-9._%+-]+@scet\.ac\.in$/,
                message: "Email must end with @scet.ac.in",
              },
            })}
          />
          {errors.email && <p>{errors.email.message}</p>}
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            {...register("password", { required: "Password is required" })}
          />
          {errors.password && <p>{errors.password.message}</p>}
        </div>
        <button type="submit">Login</button>
        <GoogleOAuthProvider clientId="313031444121-odnvb0dj5sfju6rovi1i6jbsl12o50la.apps.googleusercontent.com">
          <GoogleLogin
            onSuccess={handleGoogleSuccess}
            onFailure={handleGoogleFailure}
            buttonText="Login with Google"
          />
        </GoogleOAuthProvider>
      </form>
    </div>
  );
};
export default Login;
