import React, { useState, useEffect } from 'react';
import { getDatabase, ref, get } from 'firebase/database';
import './showstudinfo.css'; // Import the CSS file

const ShowStudInfo = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    const db = getDatabase();
    const studInfoRef = ref(db, 'studentInfo');

    get(studInfoRef)
      .then((snapshot) => {
        setLoading(false);
        if (snapshot.exists()) {
          const data = snapshot.val();
          const formattedData = Object.keys(data).map((key) => data[key]);
          setStudents(formattedData);
        } else {
          setStudents([]);
        }
      })
      .catch((error) => {
        setLoading(false);
        setError('Error fetching student info: ' + error.message);
      });
  }, []);

  return (
    <div className="show-student-info">
      <h2>Student Information</h2>
      {loading && <p className="loading-message">Loading student info...</p>}
      {error && <p className="error-message">{error}</p>}
      {students.length > 0 ? (
        <table className="students-table">
          <thead>
            <tr>
              <th>Batch Type</th>
              <th>Enrollment No</th>
              <th>Name</th>
              <th>Email</th>
              <th>Year</th>
              <th>Achievement</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student, index) => (
              <tr key={index}>
                <td>{student.batchType}</td>
                <td>{student.enrollmentNo}</td>
                <td>{student.name}</td>
                <td>{student.email}</td>
                <td>{student.year}</td>
                <td>{student.achievement}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        !loading && <p>No student info found.</p>
      )}
    </div>
  );
};

export default ShowStudInfo;
