import React, { useState } from 'react';
import { getDatabase, ref, push } from 'firebase/database';
import { getStorage, ref as storageRef, uploadBytes, getDownloadURL } from 'firebase/storage';

function AddResult() {
  const [type, setType] = useState('');
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [classname, setClassname] = useState('');
  const [schedulePdf, setSchedulePdf] = useState(null);
  const [seatingPdf, setSeatingPdf] = useState(null);
  const [resultPdf, setResultPdf] = useState(null);

  const handleFileChange = (e, setFile) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const db = getDatabase();
    const storage = getStorage();

    const uploadFile = async (file) => {
      const fileRef = storageRef(storage, `results/${file.name}`);
      await uploadBytes(fileRef, file);
      return getDownloadURL(fileRef);
    };

    const schedulePdfUrl = schedulePdf ? await uploadFile(schedulePdf) : '';
    const seatingPdfUrl = seatingPdf ? await uploadFile(seatingPdf) : '';
    const resultPdfUrl = resultPdf ? await uploadFile(resultPdf) : '';

    const resultData = {
      type,
      year,
      semester,
      classname,
      schedulePdfUrl,
      seatingPdfUrl,
      resultPdfUrl,
    };

    const resultsRef = ref(db, 'results');
    await push(resultsRef, resultData);

    setType('');
    setYear('');
    setSemester('');
    setClassname('');
    setSchedulePdf(null);
    setSeatingPdf(null);
    setResultPdf(null);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Type:</label>
        <input type="text" value={type} onChange={(e) => setType(e.target.value)} required />
      </div>
      <div>
        <label>Year:</label>
        <input type="text" value={year} onChange={(e) => setYear(e.target.value)} required />
      </div>
      <div>
        <label>Semester:</label>
        <input type="text" value={semester} onChange={(e) => setSemester(e.target.value)} required />
      </div>
      <div>
        <label>Classname:</label>
        <input type="text" value={classname} onChange={(e) => setClassname(e.target.value)} required />
      </div>
      <div>
        <label>Schedule PDF:</label>
        <input type="file" onChange={(e) => handleFileChange(e, setSchedulePdf)} />
      </div>
      <div>
        <label>Seating PDF:</label>
        <input type="file" onChange={(e) => handleFileChange(e, setSeatingPdf)} />
      </div>
      <div>
        <label>Result PDF:</label>
        <input type="file" onChange={(e) => handleFileChange(e, setResultPdf)} />
      </div>
      <button type="submit">Add Result</button>
    </form>
  );
}

export default AddResult;
