import React, { useEffect, useState } from 'react';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { useFirebaseApp } from 'reactfire';

const Converse = () => {
  const [converses, setConverses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const firebaseApp = useFirebaseApp();
  const firestore = getFirestore(firebaseApp);

  useEffect(() => {
    const fetchConverses = async () => {
      try {
        const querySnapshot = await getDocs(collection(firestore, 'converses'));
        const conversesList = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        }));
        setConverses(conversesList);
      } catch (err) {
        setError('Failed to fetch converses.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchConverses();
  }, [firestore]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="converse-list">
      <h2>Converse List</h2>
      <table>
        <thead>
          <tr>
            <th>Year</th>
            <th>Event Date</th>
            <th>Faculty Coordinator</th>
            <th>Student Coordinator</th>
            <th>Report</th>
          </tr>
        </thead>
        <tbody>
          {converses.map((converse) => (
            <tr key={converse.id}>
              <td>{converse.year}</td>
              <td>{converse.eventDate}</td>
              <td>{converse.facultyCo}</td>
              <td>{converse.studentCo}</td>
              <td>
                {converse.reportUrl ? (
                  <a href={converse.reportUrl} target="_blank" rel="noopener noreferrer">
                    View Report
                  </a>
                ) : (
                  'No Report'
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Converse;
